document.addEventListener('DOMContentLoaded', () => {
  const layoutSlider = () => {
    if (!document.getElementById('layout-slider')) return;

    const swiper = new Swiper('#layout-slider', {
      spaceBetween: 10,
      speed: 700,
      loop: true,
      watchSlidesProgress: true,
      lazy: true,
      navigation: {
        nextEl: '#layout-slider-btn-next',
        prevEl: '#layout-slider-btn-prev',
      },
      keyboard: {
        enabled: true,
        onlyInViewport: false,
      },
    });

    $('#layout-slider').lightGallery({
      thumbnail: false,
      share: false,
      selector: '.swiper-slide',
      getCaptionFromTitleOrAlt: false,
    });
  };

  layoutSlider();

  const gallerySlider = () => {
    if (!document.getElementById('gallery-slider')) return;

    const swiper = new Swiper('#gallery-slider', {
      spaceBetween: 24,
      speed: 700,
      loop: true,
      watchSlidesProgress: true,
      lazy: true,
      navigation: {
        nextEl: '#gallery-slider-btn-next',
        prevEl: '#gallery-slider-btn-prev',
      },
      breakpoints: {
        60: {
          slidesPerView: 1,
        },
        768: {
          slidesPerView: 2,
        },
        1360: {
          slidesPerView: 3,
        },
      },
      keyboard: {
        enabled: true,
        onlyInViewport: false,
      },
    });

    $('#gallery-slider').lightGallery({
      thumbnail: false,
      share: false,
      selector: '.swiper-slide',
      getCaptionFromTitleOrAlt: false,
    });
  };

  gallerySlider();
});
